function f() {
  var s11 = document.getElementById("r1-s1").value;
  var s12 = document.getElementById("r1-s2").value;
  var m1 = Math.max(s11,s12);
  var d1 = m1 - Math.min(s11,s12);
  var re1 = 5  * Math.log(1 + Math.exp(-d1 * Math.LN2 / 5.0)) / Math.LN2;
  document.getElementById("r1-r5").value = Math.round(10 * (re1 + m1)) / 10;
  var s21 = document.getElementById("r2-s1").value;
  var s22 = document.getElementById("r2-s2").value;
  var m2 = Math.max(s21,s22);
  var d2 = m2 - Math.min(s21,s22);
  var re2 = 5  * Math.log(1 + Math.exp(-d2 * Math.LN2 / 5.0)) / Math.LN2;
  document.getElementById("r2-r5").value = Math.round(10 * (re2 + m2)) / 10;
  var s31 = document.getElementById("r3-s1").value;
  var s32 = document.getElementById("r3-s2").value;
  var m3 = Math.max(s31,s32);
  var d3 = m3 - Math.min(s31,s32);
  var re3 = 5  * Math.log(1 + Math.exp(-d3 * Math.LN2 / 5.0)) / Math.LN2;
  document.getElementById("r3-r5").value = Math.round(10 * (re3 + m3)) / 10;
}
